import {useState, useEffect} from 'react';
import axios from 'axios';
import './App.css';
import CanvasDraw from 'react-canvas-draw';
import Typography from '@material-ui/core/Typography'
import AppBar from '@material-ui/core/AppBar'
import {makeStyles, ThemeProvider, createMuiTheme} from '@material-ui/core/styles';

const useStyles = makeStyles({
  title:{
    textAlign: 'center',
  }
})

function App() {
  const classes = useStyles();
  const[state, setState] = useState({
    onChange: null,
    loadTimeOffset: 0,
    lazyRadius: 5,
    brushRadius: 4,
    brushColor: "#000",
    catenaryColor: "#0a0302",
    hideGrid: true,
    canvasWidth: 280,
    canvasHeight: 280,
    saveData: null,
    hideInterface: true,
    immediateLoading: true,
  });
  const[saveableCanvas, setSaveableCanvas] = useState(null);
  const[imageData, setImageData] = useState("");
  const[response, setResponse] = useState("Loading...");

  useEffect(() => {
    var req = imageData.replaceAll("#", "%35");
    axios.get(`/process_image?image=${req}`)
      .then(res => {
        setResponse(res.data.image_prediction);
    });
  }, [imageData]);

  return (
    <div className="container">
      <AppBar>
        <Typography variant="h5" className={classes.title}>
          AI Digits
        </Typography>
      </AppBar>
      <div className="canvas">
        <CanvasDraw 
          ref={canvasDraw => (setSaveableCanvas(canvasDraw))}
          {...state}
        />
      </div>
      <button onClick={()=>{
        saveableCanvas.clear();
      }}>
        Reset
      </button>
      <button onClick={()=>{
        setImageData(saveableCanvas.getSaveData());
      }}>
        Save
      </button>
      <Typography>
        Your number is {response}
      </Typography>
    </div>
  );
}

export default App;
