import {useState} from 'react';
import './App.css';
import DrawableCanvas from 'react-drawable-canvas';

function App() {
  const[state, setState] = useState({
    brushColor: '#000',
    lineWidth: 4,
    canvasStyle: {
      backgroundColor: '#fff',
      borderColor: '#000',
      borderStyle: 'solid'
    },
    clear: false
  });

  return (
    <div className="container">
      <div className="canvas">
        <DrawableCanvas {...state}/>
      </div>
      <button onClick={() => setState({
        ...state,
        clear: true,
      })}>
        Reset
      </button>
    </div>
  );
}

export default App;
