import {useState} from 'react';
import './App.css';
import DrawableCanvas from 'react-drawable-canvas';

function App() {
  const[state, setState] = useState({
    brushColor: '#000',
    lineWidth: 4,
    width: 40,
    canvasStyle: {
      backgroundColor: '#fff',
      borderColor: '#000',
      borderStyle: 'solid'
    },
    clear: false
  });

  return (
    <div className="container">
      <DrawableCanvas {...state}/>
    </div>
  );
}

export default App;
