import {useState, useEffect} from 'react';
import axios from 'axios';
import './App.css';
import 'fontsource-roboto';
import CanvasDraw from 'react-canvas-draw';
import Typography from '@material-ui/core/Typography'
import AppBar from '@material-ui/core/AppBar'
import ToolBar from '@material-ui/core/Toolbar'
import Button from '@material-ui/core/Button'
import CssBaseline from "@material-ui/core/CssBaseline";
import {makeStyles, ThemeProvider, createMuiTheme} from '@material-ui/core/styles';
import { blue, green, orange, red } from '@material-ui/core/colors';

const useStyles = makeStyles({
  title:{
    textAlign: 'center',
  },
  subtitle:{
    textAlign: 'center',
  },
  buttonContainer:{
    marginBottom: '20px',
  },
  header:{
    flexGrow: 1,
    align: 'center',
  },
  root:{
    flexGrow: 1,
  }
})

const DarkTheme = createMuiTheme({
  palette: {
    primary: {
      main: orange[500]
    },
    secondary: {
      main: green[400]
    },
    background: {
      default: '#282c34',
    },
    text: {
      primary: '#fff',
    },
  }
})

const LightTheme = createMuiTheme({
  palette: {
    primary: {
      main: red[500]
    },
    secondary: {
      main: blue[400]
    },
    background: {
      default: '#fff',
    },
    text: {
      primary: '#282c34',
    },
  }
})

function App() {
  const classes = useStyles();
  const[state, setState] = useState({
    onChange: null,
    loadTimeOffset: 0,
    lazyRadius: 5,
    brushRadius: 4,
    brushColor: "#000",
    catenaryColor: "#0a0302",
    hideGrid: true,
    canvasWidth: 280,
    canvasHeight: 280,
    saveData: null,
    hideInterface: true,
    immediateLoading: true,
  });
  const[saveableCanvas, setSaveableCanvas] = useState(null);
  const[imageData, setImageData] = useState("");
  const[response, setResponse] = useState("Loading...");
  const[theme, setTheme] = useState(true);
  const appliedTheme = theme ? LightTheme : DarkTheme;

  useEffect(() => {
    var req = imageData.replaceAll("#", "%35");
    axios.get(`/process_image?image=${req}`)
      .then(res => {
        setResponse(res.data.image_prediction);
    });
  }, [imageData]);

  return (
    <ThemeProvider theme={appliedTheme}>
      <CssBaseline />
      <div className="container">
        <div className={classes.root}>
          <AppBar>
            <ToolBar>
              <div className={classes.header}>
                <Typography variant="h3" className={classes.title}>
                  AI Digits
                </Typography>
                <Typography variant="subtitle1" className={classes.subtitle}>
                  A very rudimentary attempt at neural networks
                </Typography>
              </div>
              <Button
                onClick={() => setTheme(!theme)}
                color = "inherit"
              >
                Toggle
              </Button>
            </ToolBar>
          </AppBar>
        </div>
        <div className="canvas">
          <CanvasDraw 
            ref={canvasDraw => (setSaveableCanvas(canvasDraw))}
            {...state}
          />
        </div>
        <div className={classes.buttonContainer}>
          <Button 
            onClick={()=>{
              saveableCanvas.clear();
            }}
            color="secondary"
            variant="outlined"
          >
            Reset
          </Button>
          <Button 
            onClick={()=>{
              setImageData(saveableCanvas.getSaveData());
            }}
            color="primary"
            variant="outlined"
          >
            Guess
          </Button>
        </div>
        <Typography variant="h5">
          Your number is {response}
        </Typography>
      </div>
    </ThemeProvider>
  );
}

export default App;
